inline long long _pow(long long a,long long n) {
	if(n<0) return 1;

	a%=mod;
	long long ret=1;

	for(;n;n>>=1,(a*=a)%=mod)
		if(n&1) (ret*=a)%=mod;

	return ret;
}

long long jie[MaxN],invj[MaxN];

long long inv(long long a,long long m) {
	if(a==1) return 1;
	return inv(m%a,m)*(m-m/a)%m;
}

inline long long getC(int a,int b) {
	if(a<b) return 0;
	if(a<=1000) return C[a][b];

	return jie[a]*invj[b]%mod*invj[a-b]%mod;
}

