inline long long cal(int n) {
	long long ret=0;

	for(int j=0;j<n;++j) {
		ret+=getC(n-1,j)*_pow(j+1,j-1)%mod*tt[n-1-j]%mod;
		if(ret>=mod) ret-=mod;
	}

	return ret;
}

